import React, { useEffect, useState } from "react";
import { Table } from 'semantic-ui-react'
import Teams from "./Teams"


export default function CompaniesList() {

  // const [error, setError] = useState(null);
  // const [isLoaded, setIsLoaded] = useState(false);

  const [items, setItems] = useState([]);
  const [teams, setTeams] = useState([]);

  const [hideCompanies, setHideCompanies] = useState(true);


  useEffect(() => {
    document.body.style.backgroundColor = "lightblue"
    getCompanies()
  }, [])


  //API CALLS 
  // Get the companies information 
  const getCompanies = async () =>  {
    const data = await fetch(process.env.REACT_APP_API_COMPANIES)
    const response = await data.json()
    setItems(response.data)
  }

  const getTeams = async (id) =>  {
    const data = await fetch(process.env.REACT_APP_API_COMPANIES+id)
    const response = await data.json()
    setItems(response.data)
  }

    // fetch(process.env.REACT_APP_API_COMPANIES)
    //   .then(res => res.json())
    //   .then(
    //     (result) => {
    //       setIsLoaded(true);
    //       setItems(result.data);
    //     },
    //     // Nota: es importante manejar errores aquí y no en 
    //     // un bloque catch() para que no interceptemos errores
    //     // de errores reales en los componentes.
    //     (error) => {
    //       setIsLoaded(true);
    //       setError(error);
    //     }
    //   )

  const trigger = (id) => {
    setHideCompanies(!hideCompanies)
    getTeams(id)
  }

  // const fetchTeams = (id) => {
  //   return fetch(process.env.REACT_APP_API_COMPANIES+id)
  //     .then(res => res.json())
  //     .then(
  //       (result) => {
  //         setTeams(result.data);
  //       },
  //       (error) => {
  //         setError(error);
  //       })
  // }

  // if (error) {
  //   return <div>Error: {error.message}</div>;
  // } 
  // else if (!isLoaded) {
  //   return <div>Loading...</div>;} 
  // else {
    return (

      <div>
        {hideCompanies &&

          <Table className="ui striped table">
            <thead>
              <tr >
                <th>Company Name</th>
                <th>Sector</th>
                <th>City </th>
                <th>Teams</th>
              </tr>
            </thead>
            <tbody>{
              items.map(item => (
                <tr key={item.CompanyId} className="center aligned">
                  <td>{item.Name}</td>
                  <td>{item.Sector}</td>
                  <td>{item.City}</td>
                  <td>
                    <button onClick={() => trigger(item.CompanyId)}>
                      <i className="eye icon"></i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>

        }

        {<div>
          {<Teams teams={teams} />}
        </div>}

      </div>
    )
  }
